import {useRef, useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";


const Main = ({ activeNote, onUpdateNote, onDeleteNote, formatDate }) => {
  const quill = useRef();

//Get the current time from web browser
  function getCurrentDateTime() {

    const now = new Date();

    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, "0");
    const day = String(now.getDate()).padStart(2, "0");
    const hours = String(now.getHours()).padStart(2, "0");
    const minutes = String(now.getMinutes()).padStart(2, "0");
    const seconds = String(now.getSeconds()).padStart(2, "0");

    const timezoneOffset = now.getTimezoneOffset() / -60;
    const timezoneOffsetFormatted = String(timezoneOffset).padStart(2, "0");
    const datetime = `${year}-${month}-${day}T${hours}:${minutes}:${seconds}-${timezoneOffsetFormatted}:00`;
    return datetime;
  }
  //Debug
  console.log(getCurrentDateTime());






  function saveNote (field, value) {
    const updatedNote = Object.assign({}, activeNote, {
      [field]: value,
      lastModified: Date.now()
    });
    onUpdateNote(updatedNote);
  }
  

  function editField (field, value) {
    const updatedNote = { ...activeNote, [field]: value, lastModified: Date.now() };
    onUpdateNote(Object.assign({}, updatedNote));
  }
  


  if (!activeNote)
    return (
      <div className="no-active-note">Select an existing note or create a new one </div>
    );


  return (
    <div className="app-main">
      <div className="app-main-note-edit">
        <div id="mainbar-header">
          <h1 id="mainbar-title">
            <input
              type="text"
              id="mainbar-title-input"
              value={activeNote.title}
              onChange={(e) => {
                onEditField("title", e.target.value);
              }}
              autoFocus
            />
          </h1>
          <div id="mainbar-buttons">
            <button
              id="save_button"
              onClick={() =>
                onSaveNote("body", quill.current.getEditor().getText())
              }
            >
              save
            </button>
            <button
              id="delete_button"
              onClick={(e) => onDeleteNote(activeNote.id)}
            >
              delete
            </button>
          </div>
        </div>


        <div id="date">
          <input
            type="datetime-local"
            id="date"
            value={new Date(activeNote.lastModified).toISOString().slice(0, -8)}
            onChange={(e) => {
              const newDate = new Date(e.target.value);
              const timestamp = newDate.getTime();
              onEditField("lastModified", timestamp);
            }}
          />
        </div>


        <div id="mainbar-content">
          <ReactQuill ref={quill} theme="snow" value={activeNote.body} />
        </div>
      </div>
    </div>
  );
};


export default Main;
